package LeetCode;

import edu.princeton.cs.algs4.StdOut;
import java.util.HashMap;
import java.util.Map;

public class FindTheDifference {
    public static void main(String[] args) {
        StdOut.println(findTheDifference("abcd", "abcde"));
        StdOut.println(findTheDifference("", "y"));
        StdOut.println(findTheDifference
                ("",
                ""));
    }

    public static char findTheDifference(String s, String t) {
        Map<Character, Integer> map = new HashMap<>(), map2 = new HashMap<>();
        int i = 0;
        while (i < s.length()) {
            if(map.containsKey(s.charAt(i))){
                map.put(s.charAt(i), map.get(s.charAt(i)) + 1);
            }else {
                map.put(s.charAt(i), 1);
            }
            i++;
        }
        i = 0;
        while (i < t.length()) {
            if(map2.containsKey(t.charAt(i))){
                map2.put(t.charAt(i), map2.get(t.charAt(i)) + 1);
            }else {
                map2.put(t.charAt(i), 1);
            }
            i++;
        }
        for (Map.Entry<Character, Integer> entry : map.entrySet()) {
            char key = entry.getKey();
            if (!map2.containsKey(key) || !map2.get(key).equals(entry.getValue())) {
                return key;
            }
        }
        for (Map.Entry<Character, Integer> entry : map2.entrySet()) {
            char key = entry.getKey();
            if (!map.containsKey(key)) {
                return key;
            }
        }
        return ' ';
    }
}
